<?phpif (get_current_blog_id() == 1) {	add_action( 'init', 'announcements_register' );		function announcements_register() {				$labels = array(			'name'               => _x( 'Announcements',				'post type general name' ),			'singular_name'      => _x( 'Announcements',				'post type singular name' ),			'add_new'            => _x( 'Add New', 'announcements' ),			'add_new_item'       => __( 'Add New Announcement' ),			'edit_item'          => __( 'Edit Announcement' ),			'new_item'           => __( 'New Announcement' ),			'view_item'          => __( 'View Announcement' ),			'search_items'       => __( 'Search Announcements' ),			'not_found'          => __( 'Nothing found' ),			'not_found_in_trash' => __( 'Nothing found in Trash' ),			'parent_item_colon'  => ''		);				$args = array(			'labels'             => $labels,			'public'             => TRUE,			'publicly_queryable' => TRUE,			'show_ui'            => TRUE,			'query_var'          => TRUE,			'capability_type'    => 'post',			'hierarchical'       => FALSE,			'menu_position'      => NULL,			'supports'           => array(				'title',				'editor',				'author',				'page-attributes',				'comments'			),			'has_archive'        => TRUE,			'show_in_rest'       => TRUE,		);				register_post_type( 'announcements', $args );			}}